enum class WidgetType {
    Vertical,
    Horizontal
}

enum class ListType {
    FIXED,
    ADAPTIVE,
    SCROLL
}

data class WidgetItem(
    val id: String = "",
    val title: String = "",
    val imageUrl: String = ""
)

@Composable
fun DynamicWidget(
    modifier: Modifier = Modifier,
    title: String = "Title",
    widgetType: WidgetType = WidgetType.Horizontal,
    listType: ListType = ListType.FIXED,
    widgetItems: List<WidgetItem> = emptyList(),
    seeAllVisible: Boolean = false,
    onSeeAllClicked: (() -> Unit)? = null
) {
    Column(modifier = modifier.fillMaxWidth()) {
        if (seeAllVisible) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                verticalAlignment = Alignment.CenterVertically,
                horizontalArrangement = Arrangement.SpaceBetween
            ) {
                TitleText(text = title)

                SubtitleText(
                    modifier = Modifier.clickable { onSeeAllClicked?.invoke() },
                    text = stringResource(id = R.string.see_all),
                    color = Blue80
                )

            }
        } else {
            TitleText(text = title)
        }

        when (widgetType) {
            WidgetType.Vertical -> TODO()
            WidgetType.Horizontal -> {
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(8.dp),
                ) {
                    Row() {
                        
                    }
                }
            }
        }

    }
}

@Preview(showBackground = true)
@Composable
fun PreviewDynamicWidget() {
    val widgets = listOf(
        "제육볶음" to "data:image/jpeg;base64,",
        "수육" to "data:image/jpeg;base64,",
        "햄버거" to "data:image/jpeg;base64,",
        "피자" to "data:image/jpeg;base64,",
        "치킨" to "data:image/jpeg;base64,"
    ).map { WidgetItem(title = it.first, imageUrl = it.second) }

    DynamicWidget(
        modifier = Modifier.padding(8.dp),
        widgetType = WidgetType.Horizontal,
        title = "추천 메뉴",
        widgetItems = widgets,
        seeAllVisible = true
    )
}